/******/ (() => {
  // webpackBootstrap
  /******/ 'use strict'; // ./src/js/files/script.js

  addEventListener('DOMContentLoaded', (event) => {
    const nextSlide = (cuurentSlide = 0) => {
      const slides = document.querySelectorAll('.slider__item');

      if (slides.length === 0) return;

      slides[cuurentSlide].classList.add('_current');

      if (cuurentSlide - 1 < 0) {
        return;
      }
      slides[cuurentSlide - 1].classList.remove('_current');
    };

    const initOclock = () => {
      let timerId;

      function update() {
        let clock = document.getElementById('clock');
        if (clock) {
          let date = new Date();

          let hours = date.getHours();
          if (hours < 10) hours = '0' + hours;
          clock.children[0].innerHTML = hours;

          let minutes = date.getMinutes();
          if (minutes < 10) minutes = '0' + minutes;
          clock.children[1].innerHTML = minutes;

          let seconds = date.getSeconds();
          if (seconds < 10) seconds = '0' + seconds;
          clock.children[2].innerHTML = seconds;

          if (Number(hours) >= 6 && Number(hours) < 12) {
            nextSlide(1);
            return;
          }
          if (Number(hours) >= 12 && Number(hours) < 18) {
            nextSlide(2);
            return;
          }
          if (Number(hours) >= 18 && Number(hours) > 0) {
            nextSlide(3);
            return;
          }

          if (Number(hours) < 6 && Number(hours) >= 0) {
            nextSlide(0);
            return;
          }
        }
      }

      function clockStart() {
        if (!timerId) {
          timerId = setInterval(update, 1000);
        }
        update();
      }

      clockStart();
    };

    const initDate = () => {
      const monthArr = [
        'Январь',
        'Февраль',
        'Март',
        'Апрель',
        'Май',
        'Июнь',
        'Июль',
        'Август',
        'Сентябрь',
        'Октябрь',
        'Ноябрь',
        'Декабрь',
      ];

      const weekDayArr = [
        'Понедельник',
        'Вторник',
        'Среда',
        'Четверг',
        'Пятница',
        'Суббота',
        'Воскресенье',
      ];
      const currentDate = document.getElementById('currentDate');
      const date = new Date();
      let day = date.getDate();
      let month = date.getMonth();
      let weekDay = date.getDay();

      if (day < 10) day = '0' + day;
      currentDate.children[0].innerHTML = day;
      currentDate.children[1].innerHTML = `${monthArr[month]},`;
      currentDate.children[2].innerHTML = weekDayArr[weekDay];
    };

    const getGeoLocation = () => {
      navigator.geolocation.getCurrentPosition(function (position) {
        debugger;
        console.log(position);
      });
    };
    initDate();
    initOclock();
    getGeoLocation();
  }); // ./src/js/app.js

  //============================================================================================================================================================================================================================================

  /******/
})();
